mapper.select:
SELECT CONCAT(travel_info_t.origin,'-',travel_info_t.destination,'-',travel_info_t.origin) AS trip, travel_info_t.cause AS cause, travel_user_t.user_name AS user_name, DATE_FORMAT(travel_user_t.gmt_go,'%Y/%m/%d') AS gmt_go, DATE_FORMAT(travel_user_t.gmt_go,'%H:%i') AS go_time_point, DATE_FORMAT(travel_user_t.gmt_back,'%Y/%m/%d') AS gmt_back, DATE_FORMAT(travel_user_t.gmt_back,'%H:%i') AS back_time_point, travel_user_t.trasportation_back AS trasportation_back, travel_user_t.trasportation_go AS trasportation_go, travel_user_t.cost_go AS cost_go, travel_user_t.cost_back AS cost_back, travel_user_t.cos_stay AS cos_stay, travel_user_t.booking_type_back AS booking_type_back, travel_user_t.booking_type_go AS booking_type_go, travel_user_t.stay_booking_type AS stay_booking_type, city_cost_standard_t.standard AS stay_one_day, user_t.user_level AS user_level, user_t.department as department, travel_user_t.stay_days as stay_days FROM travel_user_t INNER JOIN travel_info_t ON travel_info_t.travel_num = travel_user_t.travel_num INNER JOIN city_cost_standard_t ON city_cost_standard_t.city_name = travel_info_t.destination INNER JOIN user_t ON travel_user_t.user_num = user_t.user_num and gmt_back is not null
and CONCAT(YEAR(gmt_back),'年',IF(MONTH(gmt_back) < 10,CONCAT('0',MONTH(gmt_back)),MONTH(gmt_back)),'月') = #{time,jdbcType=VARCHAR}
limit #{offset,jdbcType=VARCHAR},#{size,jdbcType=VARCHAR}
